import { useEffect, useState } from "react";
import { ethers } from "ethers";
import logo from "./assets/logo1.png";
import AddOrgan from "./components/AddOrgan";
import { uploadToPinata } from "./utils/pinata";

// ABIs
import OrganNFT from "./abis/OrganNFT.json";
import OrganEscrow from "./abis/OrganEscrow.json";
import WaitingList from "./abis/WaitingList.json";
import PatientRegistry from './abis/PatientRegistry.json';

// Config
import config from "./config.json";
import Navigation from "./components/Navigation";
import Search from "./components/Search";
import Home from "./components/Home";

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [organNFT, setOrganNFT] = useState(null);
  const [waitingList, setWaitingList] = useState(null);
  const [escrow, setEscrow] = useState(null);
  const [organs, setOrgans] = useState([]);
  const [organ, setOrgan] = useState([]);
  const [toggle, setToggle] = useState(false);
  const [patientRegistry, setPatientRegistry] = useState(null);
  const [patients, setPatients] = useState([]);
  const [donorAddress, setDonorAddress] = useState(null);
  const [patientData, setPatientData] = useState({ address: "", info: "" });

  const loadBlockchainData = async () => {
    console.log("ðŸš€ loadBlockchainData called!");

    if (!window.ethereum) {
      console.error("âŒ MetaMask nu este instalat!");
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const account = await signer.getAddress();
      console.log("ðŸ”‘ Conectat cu adresa:", account);

      setAccount(account);
      setProvider(provider);

      const network = await provider.getNetwork();
      if (!config[network.chainId]) {
        console.error("âŒ Chain ID not found in config:", network.chainId);
        return;
      }
      window.ethereum.on('accountsChanged', async() => {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const account = ethers.utils.getAddress(accounts[0])
        setAccount(account);
      }); 
      const organNFTAddress = config[network.chainId]?.organNFT?.address;
      if (!organNFTAddress) {
        console.error("âŒ Adresa OrganNFT este undefined! VerificÄƒ config.json");
        return;
      }
      const organNFT = new ethers.Contract(organNFTAddress, OrganNFT, signer);
      setOrganNFT(organNFT);
      const escrow = new ethers.Contract(
        config[network.chainId].escrow.address,
        OrganEscrow,
        provider
      );

      const patientRegistry = new ethers.Contract(
        config[network.chainId].patientRegistry.address,
        PatientRegistry,
        provider
      );

      setEscrow(escrow);
      setPatientRegistry(patientRegistry);

      console.log("ðŸ“œ Contracte iniÈ›ializate!");

      const patientList = await patientRegistry.getPatientList();
      setPatients(patientList);
      console.log("âœ… Patients loaded:", patientList);

    } catch (error) {
      console.error("âŒ Eroare Ã®n loadBlockchainData:", error);
    }
  };

  const handleChange = (e) => {
    setPatientData({ ...patientData, [e.target.name]: e.target.value });
  };

  const addPatientHandler = async (e) => {
    e.preventDefault();
    if (!account) {
      alert("Please connect your wallet.");
      return;
    }

    const doctorAddress = await patientRegistry.doctor();
    if (account !== doctorAddress) {
      alert("Only the doctor can add patients.");
      return;
    }

    if (!ethers.utils.isAddress(patientData.address)) {
      alert("Invalid address.");
      return;
    }

    try {
      const signer = provider.getSigner();
      const patientRegistryWithSigner = patientRegistry.connect(signer);
      const tx = await patientRegistryWithSigner.addPatient(patientData.address, patientData.info);
      await tx.wait();
      alert(`Patient ${patientData.address} added successfully`);
      setPatients(await patientRegistry.getPatientList());
      setPatientData({ address: "", info: "" });
    } catch (error) {
      console.error("Error adding patient:", error);
      alert("Failed to add patient.");
    }
  };

  useEffect(() => {
    loadBlockchainData();
  }, []);

  return (
    <div>
      <Navigation account={account} setAccount={setAccount} />
      <Search />
      <div className="p-7 flex flex-col gap-y-6 items-center">
        <p className="text-4xl font-bold">Organs For You</p>
        {account && (
          <form onSubmit={addPatientHandler} className="flex flex-col gap-2">
            <input
              type="text"
              name="address"
              value={patientData.address}
              onChange={handleChange}
              placeholder="Patient Address"
              className="border p-2 rounded"
              required
            />
            <input
              type="text"
              name="info"
              value={patientData.info}
              onChange={handleChange}
              placeholder="Patient Info"
              className="border p-2 rounded"
              required
            />
            <button type="submit" className="bg-green-500 text-white p-2 rounded-md">Add Patient</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default App;
