import { useState, useEffect } from "react";

export default function Home({ provider, account, escrow, togglePop }) {
  const [lastNFT, setLastNFT] = useState(null);

  useEffect(() => {
    const storedNFT = localStorage.getItem("lastNFT");
    if (storedNFT) {
      setLastNFT(JSON.parse(storedNFT));
    }
  }, []);

  return (
    <div className="p-4 border rounded shadow-md w-80">
      <h2 className="text-xl font-bold mb-2">Ultimul NFT Mintat</h2>
      {lastNFT ? (
        <div className="border p-2 rounded">
          <p><strong>Organ:</strong> {lastNFT.organ}</p>
          <p><strong>Blood Type:</strong> {lastNFT.bloodType}</p>
          <p><strong>Description:</strong> {lastNFT.description}</p>
          <img src={lastNFT.image} alt="NFT" className="w-full h-auto mt-2 rounded" />
        </div>
      ) : (
        <p>Niciun NFT mintat recent.</p>
      )}
    </div>
  );
}
