import { useState, useEffect } from "react";

const Home = ({ provider, account, escrow, togglePop }) => {
  const [donor, setDonor] = useState(null);
  const [patient, setPatient] = useState(null);
  const [doctor, setDoctor] = useState(null);
  const [organId, setOrganId] = useState(null);
  const [doctorApproved, setDoctorApproved] = useState(false);
  const [currentOwner, setCurrentOwner] = useState(null);
  const [futureOwner, setFutureOwner] = useState(null);
  const [nftMetadata, setNftMetadata] = useState(null);

  // üîç Func»õie pentru a prelua detalii despre transplant
  const fetchDetails = async () => {
    const donor = await escrow.donor();
    const doctor = await escrow.doctor();
    const patient = await escrow.patient();
    const organId = await escrow.organNFT();
    const doctorApproved = await escrow.doctorApproved();
    const owners = await escrow.getOwners();

    setDonor(donor);
    setDoctor(doctor);
    setPatient(patient);
    setOrganId(organId.toString());
    setDoctorApproved(doctorApproved);
    setCurrentOwner(owners[0]);
    setFutureOwner(owners[1]);
  };

  useEffect(() => {
    fetchDetails();
  }, []);

  // üîó Func»õie pentru a ob»õine metadata NFT-ului
  const fetchNftMetadata = async () => {
    if (!organId || !escrow) return;

    try {
      const tokenURI = await escrow.organNFT().tokenURI(organId); // Ob»õine URI-ul IPFS
      const response = await fetch(tokenURI.replace("ipfs://", "https://ipfs.io/ipfs/")); // Fetch JSON
      const metadata = await response.json();

      setNftMetadata(metadata);
      console.log("üìú NFT Metadata:", metadata);
    } catch (error) {
      console.error("‚ùå Eroare la ob»õinerea metadatelor NFT:", error);
    }
  };

  // ApeleazƒÉ fetch-ul c√¢nd se schimbƒÉ `organId`
  useEffect(() => {
    if (organId) fetchNftMetadata();
  }, [organId]);

  // üè• Func»õie pentru a seta pacientul
  const setPatientHandler = async (address) => {
    try {
      if (!address) {
        alert("Patient address cannot be empty.");
        return;
      }

      const signer = provider.getSigner();
      const escrowWithSigner = escrow.connect(signer);

      const tx = await escrowWithSigner.setPatient(address);
      await tx.wait();

      alert("Patient address set successfully!");
      fetchDetails();
    } catch (error) {
      console.error("Error setting patient address:", error);
      alert("Failed to set patient address. Please try again.");
    }
  };

  // üîÅ Func»õie pentru a transfera organul
  const transferOrganHandler = async () => {
    try {
      if (!doctorApproved) {
        alert("Doctor approval is required before transferring the organ.");
        return;
      }

      const signer = provider.getSigner();
      const escrowWithSigner = escrow.connect(signer);

      await escrowWithSigner.transferOrgan();
      alert("Organ transferred successfully!");
      fetchDetails();
    } catch (error) {
      console.error("Error transferring organ:", error);
      alert("Failed to transfer organ. Please try again.");
    }
  };

  // ‚úÖ Func»õie pentru ca doctorul sƒÉ aprobe transplantul
  const approveTransplantHandler = async () => {
    try {
      const signer = provider.getSigner();
      const escrowWithSigner = escrow.connect(signer);

      await escrowWithSigner.approveTransplant();
      alert("Transplant approved successfully!");
      fetchDetails();
    } catch (error) {
      console.error("Error approving transplant:", error);
      alert("Failed to approve transplant. Please try again.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg w-11/12 md:w-[800px] p-5 relative">
        <button
          onClick={() => togglePop()}
          className="absolute -top-1 right-1 text-2xl text-gray-700 hover:text-red-500"
        >
          &times;
        </button>

        <h2 className="text-2xl font-bold text-center mb-4">Organ Transplant Details</h2>

        <p>
          <strong>Donor:</strong> {donor ? `${donor.slice(0, 6)}...${donor.slice(-4)}` : "N/A"}
        </p>
        <p>
          <strong>Patient:</strong>{" "}
          {patient ? `${patient.slice(0, 6)}...${patient.slice(-4)}` : "Not assigned yet"}
        </p>
        <p>
          <strong>Doctor:</strong> {doctor ? `${doctor.slice(0, 6)}...${doctor.slice(-4)}` : "N/A"}
        </p>
        <p>
          <strong>Organ NFT ID:</strong> {organId ? organId : "Loading..."}
        </p>
        <p>
          <strong>Doctor Approval:</strong> {doctorApproved ? "‚úÖ Approved" : "‚ùå Not Approved"}
        </p>
        <p>
          <strong>Current NFT Owner:</strong>{" "}
          {currentOwner ? `${currentOwner.slice(0, 6)}...${currentOwner.slice(-4)}` : "Loading..."}
        </p>
        <p>
          <strong>Future NFT Owner:</strong>{" "}
          {futureOwner ? `${futureOwner.slice(0, 6)}...${futureOwner.slice(-4)}` : "Not assigned yet"}
        </p>

        {/* üìú NFT Metadata Display */}
        {nftMetadata && (
          <div className="mt-4 p-3 border rounded-lg">
            <h3 className="text-lg font-semibold">Organ NFT Details</h3>
            <p><strong>Organ:</strong> {nftMetadata.organ}</p>
            <p><strong>Blood Type:</strong> {nftMetadata.BloodType}</p>
            <p><strong>Description:</strong> {nftMetadata.description}</p>
            <img src={nftMetadata.image} alt="NFT" className="w-32 h-32 mt-2" />

            <h4 className="font-semibold mt-2">Attributes:</h4>
            <ul>
              {nftMetadata.attributes.map((attr, index) => (
                <li key={index}>
                  <strong>{attr.trait_type}:</strong> {attr.value}
                </li>
              ))}
            </ul>
          </div>
        )}

        <div className="flex flex-col gap-3 mt-5">
          {account === donor && (
            <button
              onClick={() => setPatientHandler(prompt("Enter patient's address:"))}
              className="bg-blue-500 text-white p-2 rounded-md"
            >
              Set Patient
            </button>
          )}

          {account === doctor && (
            <button onClick={approveTransplantHandler} className="bg-green-500 text-white p-2 rounded-md">
              Approve Transplant
            </button>
          )}

          {account === donor && (
            <button
              onClick={transferOrganHandler}
              className={`p-2 rounded-md ${doctorApproved ? "bg-red-500 text-white" : "bg-gray-400 text-gray-700 cursor-not-allowed"}`}
              disabled={!doctorApproved}
            >
              Transfer Organ
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
