const Home = ({ organ, organs, provider, account, escrow, togglePop, findPatientByCriteria }) => {
  const [donors, setDonors] = useState([]);
  const [patients, setPatients] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [organIds, setOrganIds] = useState([]);
  const [doctorApproved, setDoctorApproved] = useState([]);
  const [currentOwners, setCurrentOwners] = useState([]);
  const [futureOwners, setFutureOwners] = useState([]);
  const [patientSet, setPatientSet] = useState(false);

  // Func»õie pentru a prelua detalii despre toate contractele escrow
  const fetchDetails = async () => {
    const details = await Promise.all(
      escrow.map(async (escrowContract) => {
        const donor = await escrowContract.donor();
        const doctor = await escrowContract.doctor();
        const patient = await escrowContract.patient();
        const organId = await escrowContract.organNFT();
        const doctorApproved = await escrowContract.doctorApproved();
        const owners = await escrowContract.getOwners();

        return {
          donor,
          doctor,
          patient,
          organId: organId.toString(),
          doctorApproved,
          currentOwner: owners[0],
          futureOwner: owners[1],
        };
      })
    );

    setDonors(details.map(d => d.donor));
    setDoctors(details.map(d => d.doctor));
    setPatients(details.map(d => d.patient));
    setOrganIds(details.map(d => d.organId));
    setDoctorApproved(details.map(d => d.doctorApproved));
    setCurrentOwners(details.map(d => d.currentOwner));
    setFutureOwners(details.map(d => d.futureOwner));
  };

  useEffect(() => {
    fetchDetails();
  }, [escrow]);


  useEffect(() => {
    const checkIfPatientIsSet = async () => {
      const isSet = await escrow.isPatientSet();
      if (!isSet && !patientSet) {
        const foundPatient = await findPatientByCriteria({ bloodType: "B+" });
        if (foundPatient) {
          console.log("üîç Pacient gƒÉsit automat:", foundPatient);
          await setPatientHandler(foundPatient.address);
          setPatientSet(true);
        } else {
          console.log("‚ö†Ô∏è Niciun pacient potrivit gƒÉsit.");
        }
      }
    };

    fetchDetails();
    checkIfPatientIsSet();
  }, [patientSet]);

  // Func»õie pentru a seta adresa pacientului
  const setPatientHandler = async (address) => {
    try {
      if (!address) {
        alert("Patient address cannot be empty.");
        return;
      }

      console.log("üîπ Setting patient to:", address);
      const signer = provider.getSigner();
      const escrowWithSigner = escrow.connect(signer);
      const tx = await escrowWithSigner.setPatient(address);
      await tx.wait();
      alert("Patient address set successfully!");
      fetchDetails();
    } catch (error) {
      console.error("Error setting patient address:", error);
      alert("Failed to set patient address. Please try again.");
    }
  };

  // Func»õie pentru a transfera organul
  const transferOrganHandler = async () => {
    try {
      if (!doctorApproved) {
        alert("Doctor approval is required before transferring the organ.");
        return;
      }

      const signer = provider.getSigner();
      const escrowWithSigner = escrow.connect(signer);
      const tx = await escrowWithSigner.transferOrgan();
      await tx.wait();
      setCurrentOwner(futureOwner);
      setFutureOwner(null);
      alert("Organ transferred successfully!");
      fetchDetails();
    } catch (error) {
      console.error("Error transferring organ:", error);
      alert("Failed to transfer organ. Please try again.");
    }
  };

  // Func»õie pentru ca doctorul sƒÉ aprobe transplantul
  const approveTransplantHandler = async () => {
    try {
      const signer = provider.getSigner();
      const escrowWithSigner = escrow.connect(signer);
      const tx = await escrowWithSigner.approveTransplant();
      await tx.wait();
      setDoctorApproved(true);
      alert("Transplant approved successfully!");
      fetchDetails();
    } catch (error) {
      console.error("Error approving transplant:", error);
      alert("Failed to approve transplant. Please try again.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg w-11/12 md:w-[800px] p-5 relative">
        <button onClick={() => togglePop()} className="absolute -top-1 right-1 text-2xl text-gray-700 hover:text-red-500">
          &times;
        </button>

        <h2 className="text-2xl font-bold text-center mb-4">Organ Transplant Details</h2>

        <p><strong>Donor:</strong> {donor ? `${donor.slice(0, 6)}...${donor.slice(-4)}` : "N/A"}</p>
        <p><strong>Patient:</strong> {patient ? `${patient.slice(0, 6)}...${patient.slice(-4)}` : "Not assigned yet"}</p>
        <p><strong>Doctor:</strong> {doctor ? `${doctor.slice(0, 6)}...${doctor.slice(-4)}` : "N/A"}</p>
        <p><strong>Organ NFT ID:</strong> {organId ? organId : "Loading..."}</p>
        <p><strong>Doctor Approval:</strong> {doctorApproved ? "‚úÖ Approved" : "‚ùå Not Approved"}</p>
        <p><strong>Current NFT Owner:</strong> {currentOwner ? `${currentOwner.slice(0, 6)}...${currentOwner.slice(-4)}` : "Loading..."}</p>
        <p><strong>Future NFT Owner:</strong> {futureOwner ? `${futureOwner.slice(0, 6)}...${futureOwner.slice(-4)}` : "Not assigned yet"}</p>

        {/* Afi»ôeazƒÉ toate NFT-urile */}
        <div className="mt-5">
          <h3 className="text-xl font-bold mb-4">All Organs</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {organs.map((organ, index) => (
              <div key={index} className="border rounded-lg p-4">
                <img src={organ.image || "fallback.jpg"} className="w-full h-auto rounded-t-lg" />
                <p><strong>Organ:</strong> {organ.organ || "N/A"}</p>
                <p><strong>Blood Type:</strong> {organ.bloodType || organ.attributes?.find(attr => attr.trait_type === "Blood Type")?.value || "N/A"}</p>
                <p><strong>Description:</strong> {organ.description || "No description"}</p>
                <div className="mt-2">
                  <strong>Attributes:</strong>
                  <ul>
                    {organ.attributes?.map((attr, i) => (
                      <li key={i}>{attr.trait_type}: {attr.value}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="flex flex-col gap-3 mt-5">
          {account === doctor && (
            <button onClick={approveTransplantHandler} className="bg-green-500 text-white p-2 rounded-md">
              Approve Transplant
            </button>
          )}

          {account === donor && (
            <button onClick={transferOrganHandler} className={`p-2 rounded-md ${doctorApproved ? "bg-red-500 text-white" : "bg-gray-400 text-gray-700 cursor-not-allowed"}`} disabled={!doctorApproved}>
              Transfer Organ
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
