import { useState, useEffect } from 'react';

const Home = ({ togglePop }) => {
  const [nftData, setNftData] = useState(null);

  useEffect(() => {
    // Preluăm datele NFT din localStorage
    const savedNftData = localStorage.getItem("nftData");
    if (savedNftData) {
      setNftData(JSON.parse(savedNftData));
    }
  }, []);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg w-11/12 md:w-[800px] p-5 relative">
        <button onClick={() => togglePop()} className="absolute -top-1 right-1 text-2xl text-gray-700 hover:text-red-500">&times;</button>
        <h2 className="text-2xl font-bold text-center mb-4">Organ Transplant Details</h2>
        {nftData ? (
          <>
            <p><strong>Organ:</strong> {nftData.organ}</p>
            <p><strong>Blood Type:</strong> {nftData.BloodType}</p>
            <p><strong>Description:</strong> {nftData.description}</p>
            <p><strong>Image:</strong></p>
            <img src={nftData.image} alt="Organ NFT" className="w-full max-w-xs mx-auto" />
            <h3 className="text-xl font-semibold mt-4">NFT Attributes</h3>
            <ul>
              {nftData.attributes.map((attr, index) => (
                <li key={index}><strong>{attr.trait_type}:</strong> {attr.value}</li>
              ))}
            </ul>
          </>
        ) : (
          <p>No NFT data available. Please add an organ first.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
