import { useState } from "react";
import AddOrgan from "./AddOrgan";
import Home from "./Home";

export default function App({ organNFT, provider, account, donorAddress }) {
  const [refreshNFTs, setRefreshNFTs] = useState(false);

  const handleNFTAdded = () => {
    setRefreshNFTs(!refreshNFTs); // Trigger re-fetching NFTs
  };

  return (
    <div>
      <AddOrgan
        organNFT={organNFT}
        provider={provider}
        account={account}
        donorAddress={donorAddress}
        onNFTAdded={handleNFTAdded}
      />
      <Home organNFT={organNFT} key={refreshNFTs} />
    </div>
  );
}
