import { useEffect, useState } from "react";
import { ethers } from "ethers";
import Navigation from "./components/Navigation";
import Search from "./components/Search";
import Home from "./components/Home";
import config from "./config.json";
import PatientRegistry from "./abis/PatientRegistry.json";

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [patientRegistry, setPatientRegistry] = useState(null);
  const [patients, setPatients] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [patientAddress, setPatientAddress] = useState("");
  const [patientInfo, setPatientInfo] = useState("");

  const loadBlockchainData = async () => {
    if (!window.ethereum) {
      console.error("MetaMask nu este instalat!");
      return;
    }
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const account = await signer.getAddress();
      setAccount(account);
      setProvider(provider);

      const network = await provider.getNetwork();
      const patientRegistry = new ethers.Contract(
        config[network.chainId].patientRegistry.address,
        PatientRegistry,
        provider
      );
      setPatientRegistry(patientRegistry);

      const patientList = await patientRegistry.getPatientList();
      setPatients(patientList);
    } catch (error) {
      console.error("Eroare în loadBlockchainData:", error);
    }
  };

  useEffect(() => {
    loadBlockchainData();
  }, []);

  const addPatientHandler = async (e) => {
    e.preventDefault();
    if (!account) {
      alert("Please connect your wallet.");
      return;
    }
    try {
      const signer = provider.getSigner();
      const patientRegistryWithSigner = patientRegistry.connect(signer);
      const tx = await patientRegistryWithSigner.addPatient(patientAddress, patientInfo);
      await tx.wait();
      alert(`Patient ${patientAddress} added successfully`);
      setPatients([...patients, patientAddress]);
      setShowForm(false);
      setPatientAddress("");
      setPatientInfo("");
    } catch (error) {
      console.error("Error adding patient:", error);
      alert("Failed to add patient.");
    }
  };

  return (
    <div>
      <Navigation account={account} setAccount={setAccount} />
      <Search />

      <div className="p-7 flex flex-col gap-y-6 items-center">
        <p className="text-4xl font-bold">Patients</p>

        {account && (
          <button
            onClick={() => setShowForm(true)}
            className="bg-green-500 text-white p-2 rounded-md"
          >
            Add Patient
          </button>
        )}

        {showForm && (
          <form onSubmit={addPatientHandler} className="bg-gray-200 p-5 rounded-md mt-3">
            <label className="block mb-2">Patient Address:</label>
            <input
              type="text"
              value={patientAddress}
              onChange={(e) => setPatientAddress(e.target.value)}
              className="p-2 border rounded-md w-full"
              required
            />
            <label className="block mb-2 mt-3">Patient Information:</label>
            <textarea
              value={patientInfo}
              onChange={(e) => setPatientInfo(e.target.value)}
              className="p-2 border rounded-md w-full"
              required
            ></textarea>
            <button
              type="submit"
              className="bg-blue-500 text-white p-2 rounded-md mt-3 w-full"
            >
              Submit
            </button>
          </form>
        )}

        <div className="mt-5">
          <h3 className="text-xl font-bold">Patients List</h3>
          <ul className="list-disc mt-2">
            {patients.length === 0 ? (
              <li>No patients added yet.</li>
            ) : (
              patients.map((patient, index) => (
                <li key={index}>{`${patient.slice(0, 6)}...${patient.slice(-4)}`}</li>
              ))
            )}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
