import { uploadToPinata } from "../utils/pinata";
import { useState } from "react";
import { ethers } from "ethers";

export default function AddOrgan({ organNFT, provider, account, donorAddress, reloadData }) {
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [organData, setOrganData] = useState({
    organ: "",
    bloodType: "",
    description: "",
    image: "",
  });

  // ðŸ“ ActualizÄƒm datele cÃ¢nd utilizatorul scrie Ã®n formular
  const handleChange = (e) => {
    setOrganData({ ...organData, [e.target.name]: e.target.value });
  };

  const handleAddOrgan = async () => {
    if (!donorAddress) {
      alert("Donor address not found!");
      return;
    }

    if (!organNFT || !provider || !account || !donorAddress) {
      alert("Contractul sau wallet-ul nu sunt iniÈ›ializate!");
      return;
    }

    if (account.toLowerCase() !== donorAddress.toLowerCase()) {
      alert("Numai donor-ul poate mina NFT-uri!");
      return;
    }

    setLoading(true);

    const nftData = {
      ...organData,
      id: Date.now().toString(),
      attributes: [
        { trait_type: "Donor", value: account },
        { trait_type: "Organ", value: organData.organ },
        { trait_type: "Blood Type", value: organData.bloodType },
      ],
    };

    // ðŸ”— ÃŽncÄƒrcÄƒm metadata pe IPFS
    const ipfsUrl = await uploadToPinata(nftData);
    if (!ipfsUrl) {
      alert("Eroare la Ã®ncÄƒrcare pe IPFS!");
      setLoading(false);
      return;
    }

    try {
      const signer = provider.getSigner();
      const organNFTWithSigner = organNFT.connect(signer);

      // ðŸ”¥ Mint NFT ca donor
      const mintTx = await organNFTWithSigner.mintOrganNFT(ipfsUrl);
      await mintTx.wait();

      alert("NFT mintat cu succes!");
      reloadData(); // ActualizÄƒm UI
    } catch (error) {
      console.error("Eroare la minting:", error);
      alert("Minting failed!");
    }

    setLoading(false);
    setShowForm(false); // ÃŽnchidem formularul dupÄƒ minting
  };

  return (
    <div>
      <button
        onClick={() => setShowForm(true)}
        className="bg-blue-500 text-white p-2 rounded-md mt-3"
      >
        Add Organ
      </button>

      {showForm && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-75">
          <div className="bg-white p-5 rounded-lg shadow-lg w-[400px]">
            <h2 className="text-xl font-bold mb-4">CompleteazÄƒ detaliile organului</h2>

            <input
              type="text"
              name="organ"
              placeholder="Organ (ex: Rinichi)"
              className="w-full p-2 border rounded mb-2"
              value={organData.organ}
              onChange={handleChange}
            />

            <input
              type="text"
              name="bloodType"
              placeholder="Blood Type (ex: A+)"
              className="w-full p-2 border rounded mb-2"
              value={organData.bloodType}
              onChange={handleChange}
            />

            <textarea
              name="description"
              placeholder="Descriere"
              className="w-full p-2 border rounded mb-2"
              value={organData.description}
              onChange={handleChange}
            />

            <input
              type="text"
              name="image"
              placeholder="Image URL (optional)"
              className="w-full p-2 border rounded mb-2"
              value={organData.image}
              onChange={handleChange}
            />

            <div className="flex justify-between">
              <button
                onClick={() => setShowForm(false)}
                className="bg-gray-500 text-white p-2 rounded"
              >
                AnuleazÄƒ
              </button>
              <button
                onClick={handleAddOrgan}
                className="bg-green-500 text-white p-2 rounded"
              >
                {loading ? "Minting..." : "ConfirmÄƒ"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
