import { useState, useEffect } from "react";

const Home = ({ provider, account, escrow, togglePop }) => {
  const [organData, setOrganData] = useState(null);

  useEffect(() => {
    const savedData = localStorage.getItem("organData");
    if (savedData) {
      setOrganData(JSON.parse(savedData));
    }
  }, []);

  return (
    <div className="p-4 border rounded-md shadow-md">
      <h2 className="text-xl font-bold mb-4">Organ NFT</h2>
      {organData ? (
        <div>
          <p><strong>Organ:</strong> {organData.organ}</p>
          <p><strong>Blood Type:</strong> {organData.bloodType}</p>
          <p><strong>Description:</strong> {organData.description}</p>
          <img src={organData.image} alt="Organ" className="w-32 h-32 object-cover mt-2" />
          <p><strong>IPFS:</strong> <a href={organData.ipfsUrl} target="_blank">View on IPFS</a></p>
        </div>
      ) : (
        <p>No NFT data available.</p>
      )}
    </div>
  );
};

export default Home;
